// =====================================================
// PROCESSAMENTO E VALIDA√á√ÉO ROBUSTA - v3.0
// =====================================================

const body = $input.item.json.body || $input.item.json;

// ===== TESTE DE CONEX√ÉO =====
if (body.test === true) {
  return [{
    json: {
      success: true,
      message: '‚úÖ Conex√£o OK!',
      version: '3.0',
      features: ['WebSocket', 'Analytics', 'RateLimit', 'Retry', 'Validation'],
      timestamp: new Date().toISOString()
    }
  }];
}

// ===== EXTRAIR E VALIDAR DADOS =====
const contacts = body.contacts || [];
const config = body.config || {};
const metadata = body.metadata || {};

// Valida√ß√µes cr√≠ticas
if (!contacts || contacts.length === 0) {
  throw new Error('‚ùå Nenhum contato encontrado no payload');
}

if (contacts.length > 10000) {
  throw new Error('‚ùå M√°ximo de 10.000 contatos por campanha');
}

const apiKey = config.evolutionApiKey || config.apiKey;
const endpoint = config.evolutionEndpoint;
const backendUrl = config.backendUrl || 'https://rapidflow-backend.onrender.com';
const useAI = config.useAI !== false; // Default: true
const maxRetries = Math.min(config.maxRetries || 3, 5); // Max 5 retries
const delayMin = Math.max(config.delayMin || 140, 100); // Min 100ms
const delayMax = Math.min(config.delayMax || 380, 3600); // Max 1h

if (!apiKey) throw new Error('‚ùå API Key da Evolution n√£o fornecida');
if (!endpoint) throw new Error('‚ùå Evolution endpoint n√£o fornecido');
if (delayMin > delayMax) throw new Error('‚ùå delayMin n√£o pode ser maior que delayMax');

console.log('\n' + '='.repeat(70));
console.log('üöÄ NOVA CAMPANHA INICIADA - ENHANCED v3.0');
console.log('='.repeat(70));
console.log(`üìä Campaign ID: ${metadata.campaignId}`);
console.log(`üë• Total de Contatos: ${contacts.length}`);
console.log(`ü§ñ IA Ativada: ${useAI ? 'SIM ‚úÖ' : 'N√ÉO ‚ùå'}`);
console.log(`üîÑ Max Retries: ${maxRetries}`);
console.log(`‚è±Ô∏è  Delay Range: ${delayMin}ms - ${delayMax}ms`);
console.log(`üîô Backend URL: ${backendUrl}`);
console.log(`üì° Evolution: ${endpoint}`);
console.log('='.repeat(70) + '\n');

// ===== PROCESSAR CONTATOS COM VALIDA√á√ÉO =====
const validContacts = [];
const invalidContacts = [];

contacts.forEach((contact, index) => {
  // Validar nome
  if (!contact.nome || typeof contact.nome !== 'string' || contact.nome.trim().length === 0) {
    invalidContacts.push({ index, reason: 'Nome inv√°lido', contact });
    return;
  }

  // Validar telefone
  if (!contact.telefone || typeof contact.telefone !== 'string') {
    invalidContacts.push({ index, reason: 'Telefone inv√°lido', contact });
    return;
  }

  // Normalizar telefone
  let telefone = String(contact.telefone).replace(/[^0-9]+/g, '');

  // Validar tamanho
  if (telefone.length < 10 || telefone.length > 15) {
    invalidContacts.push({ index, reason: 'Telefone com tamanho inv√°lido', contact });
    return;
  }

  // Adicionar c√≥digo do pa√≠s se necess√°rio
  if (telefone.length <= 11) {
    telefone = '55' + telefone;
  }

  // Calcular delay com varia√ß√£o
  const delay = Math.floor(Math.random() * (delayMax - delayMin + 1)) + delayMin;

  validContacts.push({
    id: validContacts.length + 1,
    nome: contact.nome.trim(),
    telefone: telefone,

    // Config Evolution
    evolutionEndpoint: endpoint,
    apiKey: apiKey,
    imageUrl: config.imageUrl || '',

    // Config OpenAI
    openaiKey: config.openaiApiKey || '',
    openaiModel: config.openaiModel || 'gpt-4.1-mini',
    systemPrompt: config.systemPrompt || 'Ol√°!',
    useAI: useAI,

    // Controle de envio
    delay_calculado: delay,
    maxRetries: maxRetries,
    currentRetry: 0,
    total: contacts.length,
    validTotal: 0, // Ser√° preenchido depois
    is_first: false,
    is_last: false,

    // Metadata
    campaignId: metadata.campaignId,
    backendUrl: backendUrl,
    startTime: metadata.startTime || new Date().toISOString(),

    // Controle de circuito
    consecutiveErrors: 0,
    circuitBreakerOpen: false
  });
});

// Atualizar contadores
const validTotal = validContacts.length;
validContacts.forEach((contact, index) => {
  contact.validTotal = validTotal;
  contact.is_first = index === 0;
  contact.is_last = index === validTotal - 1;
});

// Log de contatos inv√°lidos
if (invalidContacts.length > 0) {
  console.log(`\n‚ö†Ô∏è  ATEN√á√ÉO: ${invalidContacts.length} contatos inv√°lidos foram removidos:`);
  invalidContacts.slice(0, 5).forEach(item => {
    console.log(`  - √çndice ${item.index}: ${item.reason}`);
  });
  if (invalidContacts.length > 5) {
    console.log(`  ... e mais ${invalidContacts.length - 5} contatos`);
  }
  console.log('');
}

console.log(`‚úÖ Contatos v√°lidos: ${validTotal}/${contacts.length}`);
console.log(`üìã Tempo estimado: ~${Math.round((validTotal * ((delayMin + delayMax) / 2)) / 60000)} minutos\n`);

if (validContacts.length === 0) {
  throw new Error('‚ùå Nenhum contato v√°lido para processar');
}

return validContacts.map(contact => ({ json: contact }));

// Circuit Breaker Check
const item = $input.item.json;

if (item.circuitBreakerOpen) {
  console.log(`‚õî Circuit breaker ABERTO para campanha ${item.campaignId}`);
  return [{
    json: {
      ...item,
      skipMessage: true,
      error: 'Circuit breaker ativo - muitos erros consecutivos'
    }
  }];
}

return $input.all();

// Mensagem simples sem IA
const item = $input.item.json;
let mensagem = item.systemPrompt;

// Substituir vari√°veis
mensagem = mensagem.replace(/\{\{nome\}\}/gi, item.nome);
mensagem = mensagem.replace(/\{\{telefone\}\}/gi, item.telefone);

return [{
  json: {
    ...item,
    mensagem_gerada: mensagem.trim(),
    ai_used: false
  }
}];

// P√≥s-processar IA
const item = $input.item.json;
const aiOutput = $('AI Agent - Gerar Mensagem').item.json.output;

let mensagem = aiOutput.trim();
mensagem = mensagem.replace(/^[\"']|[\"']$/g, '');

return [{
  json: {
    ...item,
    mensagem_gerada: mensagem,
    ai_used: true
  }
}];

// Notificar progresso a cada 5 mensagens
const item = $input.item.json;

if (item.id % 5 === 0 || item.is_last) {
  const progressData = {
    currentPosition: item.id,
    sent: item.id,
    errors: 0
  };

  return [{
    json: {
      ...item,
      progressData,
      shouldNotifyProgress: true
    }
  }];
}

return $input.all();

// Retry com exponential backoff
const item = $input.item.json;
const retryNumber = (item.currentRetry || 0) + 1;
const baseDelay = 2000; // 2s
const retryDelay = baseDelay * Math.pow(2, retryNumber - 1); // Exponential backoff

console.log(`üîÑ Retry ${retryNumber}/${item.maxRetries} para ${item.nome} (aguardar ${retryDelay}ms)`);

return [{
  json: {
    ...item,
    currentRetry: retryNumber,
    retryDelay: retryDelay
  }
}];

const item = $input.item.json;
const sent = !item.error;

if (sent) {
  console.log(`   ‚úÖ [${item.id}/${item.validTotal}] ${item.nome} - ENVIADO`);
} else {
  console.log(`   ‚ùå [${item.id}/${item.validTotal}] ${item.nome} - ERRO: ${item.error}`);
}

if (item.is_last) {
  console.log('\nüéØ √öLTIMO CONTATO PROCESSADO!\n');
}

return $input.all();

const item = $input.item.json;

if (item.is_last) {
  console.log('\n' + '='.repeat(70));
  console.log('‚úÖ CAMPANHA CONCLU√çDA COM SUCESSO!');
  console.log('='.repeat(70));
  console.log(`üìä Campaign ID: ${item.campaignId}`);
  console.log(`üë• Total Processado: ${item.validTotal}`);
  console.log(`‚è∞ In√≠cio: ${item.startTime}`);
  console.log(`‚è∞ Fim: ${new Date().toISOString()}`);
  console.log('='.repeat(70) + '\n');
}

return $input.all();
